macro generate_expression ()
{
  compute_some_expression ();
}

macro m () {
  printf ("compile-time\n");
  <[ printf ("run-time\n") ]>
}

module M {
  public Main () : void {
    m ();
  }
}

compute_some_expression () : Expr 
{
  if (debug_on) 
    <[ System.Console.WriteLine ("Hello, I'm debug message") ]>
  else
    <[ () ]>
}

macro print_date (at_compile_time)
{                   
  match (at_compile_time) {
    | <[ true ]> => print_compilation_time ()
    | _ => <[ WriteLine (DateTime.Now.ToString ()) ]>
  }
}

macro for (init, cond, change, body)
{
  <[ 
    $init;
    def loop () : void {
      if ($cond) { $body; $change; loop() } 
      else ()
    };
    loop ()
  ]>
}

mutable exps <- [ <[ printf ("%d ", x) ]>, <[ printf ("%d ", y) ]> ];
exps <- <[ def x = 1 ]> :: <[ def y = 2 ]> :: exps;
<[ {.. $exps } ]>

macro castedarray (e) {
 match (e) {
  | <[ array [.. $elements ] ]> =>
     def casted = List.Map (elements, fun (x) {<[ ($x :> object) ]>});
     <[ array [.. $casted] ]>
  | _ => e
}

for (mutable i <- 0; i < n; i <- i + 1) {
  printf ("%d\n", i);
  sum <- sum + i;
}
